  const [reservas, setReservas] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [editModalIsOpen, setEditModalIsOpen] = useState(false);
  const [selectedReserva, setSelectedReserva] = useState(null);

  useEffect(() => {
    // Lógica para obtener las reservas desde la API
    axios.get('https://api-node-bus.onrender.com/api/reservas')
      .then(response => setReservas(response.data))
      .catch(error => console.error('Error al obtener las reservas:', error));
  }, []);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const handleUpdateReserva = async (e) => {
    e.preventDefault();
  
    try {
      // Logic to send the update request to the API
      // Use axios.put or axios.patch to send the updated data
      // Remember to handle errors and close the modal after updating
      console.log('Reserva updated');
      closeEditModal();
    } catch (error) {
      console.error('Error updating reservation:', error);
    }
  };
  const handleInputChange = (e, field) => {
    setSelectedReserva({
      ...selectedReserva,
      [field]: e.target.value,
    });
  };
  

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const fetchData = async () => {
    try {
      const reservasData = await axios.get('https://api-node-bus.onrender.com/api/reservas');
      const reservas = reservasData.data;

      const updatedReservas = await Promise.all(reservas.map(async (reserva) => {
        try {
          const asientoResponse = await axios.get(`https://api-node-bus.onrender.com/api/asientos/${reserva.asientoId}`);
          const usuarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/usuarios/${reserva.usuarioId}`);
          const horarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/horarios/${reserva.horarioId}`);

          return {
            ...reserva,
            asientoId: asientoResponse.data.n_asiento,
            usuarioId: usuarioResponse.data.nombre,
            horarioId: horarioResponse.data.hora_inicio,
          };
        } catch (error) {
          console.error('Error al obtener información adicional:', error);
          return null;
        }
      }));


      
      setReservas(updatedReservas.filter(Boolean)); // Filtra los elementos nulos
    } catch (error) {
      console.error('Error al obtener las reservas:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []); 

  const handleClickEliminar = () => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este elemento?')) {
      // Lógica para eliminar el elemento
      console.log('Elemento eliminado');
    } else {
      console.log('Eliminación cancelada');
    }
  };

  const closeEditModal = () => {
    setSelectedReserva(null);
    setEditModalIsOpen(false);
  };


  const handleAddReserva = async (e) => {
    e.preventDefault();
  
    try {
      // Lógica para enviar la solicitud de creación a la API
      const nuevaReserva = {
        Fecha: selectedReserva.Fecha,
        qr: selectedReserva.qr,
        asientoId: selectedReserva.asientoId,
        usuarioId: selectedReserva.usuarioId,
        horarioId: selectedReserva.horarioId,
        // Ajusta según la estructura de tu objeto reserva y los campos del formulario
      };
  
      // Realiza la solicitud POST para agregar la nueva reserva
      await axios.post('https://api-node-bus.onrender.com/api/reservas', nuevaReserva);
  
      // Refresca la lista de reservas
      fetchData();
  
      // Cierra el modal después de agregar la reserva
      closeModal();
    } catch (error) {
      console.error('Error al agregar la reserva:', error);
    }
  };
  

  const handleClickFlecha = () => {
    // Lógica para manejar el clic en el botón "Eliminar"
  };

  const openEditModal = async (reserva) => {
    try {
      const asientoResponse = await axios.get(`https://api-node-bus.onrender.com/api/asientos/${reserva.asientoId}`);
      const usuarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/usuarios/${reserva.usuarioId}`);
      const horarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/horarios/${reserva.horarioId}`);
  
      const updatedReserva = {
        ...reserva,
        asientoId: asientoResponse.data.n_asiento,
        usuarioId: usuarioResponse.data.nombre,
        horarioId: horarioResponse.data.hora_inicio,
      };
  
      setSelectedReserva(updatedReserva);
      setEditModalIsOpen(true);
    } catch (error) {
      console.error('Error al obtener información adicional:', error);
    }
  };

  return (
    <div className="inicio-container">
      <h2>Paraderos de BusTecsup</h2>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th colSpan="5">
                Buses
                <button className='btn1' onClick={openModal}>+ Añadir Paradero</button>
              </th>
            </tr>
            <tr>
              <th>Fecha<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>qr<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Número de Asiento<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Estudiante<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Horario<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Editar&nbsp;&nbsp;&nbsp;&nbsp;Eliminar</th>
            </tr>
          </thead>
          <tbody>
            {reservas.map(reserva => (
              <tr key={reserva._id}>
                <td>{new Date(reserva.Fecha).toLocaleDateString()}</td>
                <td>{reserva.qr}</td>
                <td>{reserva.asientoId}</td>
                <td>{reserva.usuarioId}</td>
                <td>{reserva.horarioId}</td>
                <td>
                  <button className='img' onClick={() => openEditModal(reserva)}>
                    <img className='img' src={editarImg} alt="Editar" />
                  </button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <button className='img' onClick={() => handleClickEliminar(reserva._id)}>
                    <img className='img' src={eliminarImg} alt="Eliminar" />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {modalIsOpen && (
  <div className="modal">
    <div className="modal-content">
      <span className="close" onClick={closeModal}>
        &times;
      </span>
      <h1>Registro de Paradero</h1>
      <form onSubmit={handleAddReserva}>
        <div className="form-group">
          <label>Fecha:</label>
          <input
            type="date"
            value={selectedReserva?.Fecha || ''}
            onChange={(e) => handleInputChange(e, 'Fecha')}
          />
        </div>
        <div className="form-group">
          <label>QR:</label>
          <input
            type="text"
            value={selectedReserva?.qr || ''}
            onChange={(e) => handleInputChange(e, 'qr')}
          />
        </div>
        <div className="form-group">
          <label>Número de Asiento:</label>
          <input
            type="text"
            value={selectedReserva?.asientoId || ''}
            onChange={(e) => handleInputChange(e, 'asientoId')}
          />
        </div>
        <div className="form-group">
          <label>Estudiante:</label>
          <input
            type="text"
            value={selectedReserva?.usuarioId || ''}
            onChange={(e) => handleInputChange(e, 'usuarioId')}
          />
        </div>
        <div className="form-group">
          <label>Horario:</label>
          <input
            type="text"
            value={selectedReserva?.horarioId || ''}
            onChange={(e) => handleInputChange(e, 'horarioId')}
          />
        </div>
        <div className="form-group">
          <button className="submit1" type="submit">Agregar</button>
          <button className="submit2" type="button" onClick={closeModal}>Cancelar</button>
        </div>
      </form>
    </div>
  </div>
)}


{editModalIsOpen && (
  <div className="modal">
    <div className="modal-content">
      <span className="close" onClick={closeEditModal}>
        &times;
      </span>
      <h1>Actualizar Paradero</h1>
      <form onSubmit={handleUpdateReserva}>
        <div className="form-group">
          <label>Fecha:</label>
          <input
            type="date"
            value={selectedReserva.Fecha}
            onChange={(e) => handleInputChange(e, 'Fecha')}
          />
        </div>
        <div className="form-group">
          <label>QR:</label>
          <input
            type="text"
            value={selectedReserva.qr}
            onChange={(e) => handleInputChange(e, 'qr')}
          />
        </div>
        <div className="form-group">
          <label>Número de Asiento:</label>
          <input
            type="text"
            value={selectedReserva.asientoId}
            onChange={(e) => handleInputChange(e, 'asientoId')}
          />
        </div>
        <div className="form-group">
          <label>Estudiante:</label>
          <input
            type="text"
            value={selectedReserva.usuarioId}
            onChange={(e) => handleInputChange(e, 'usuarioId')}
          />
        </div>
        <div className="form-group">
          <label>Horario:</label>
          <input
            type="text"
            value={selectedReserva.horarioId}
            onChange={(e) => handleInputChange(e, 'horarioId')}
          />
        </div>
        <div className="form-group">
          <button className="submit1" type="submit">Actualizar</button>
          <button className="submit2" type="button" onClick={closeEditModal}>Cancelar</button>
        </div>
      </form>
    </div>
  </div>
)}
    </div>
  );
}

export default Reserva;
