import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/registro.css';
import jsPDF from 'jspdf';

const Reserva = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://api-node-bus.onrender.com/api/reservas');
      const reservas = response.data;

      const updatedReservas = await Promise.all(
        reservas.map(async (reserva) => {
          try {
            const asientoResponse = await axios.get(`https://api-node-bus.onrender.com/api/asientos/${reserva.asientoId}`);
            const usuarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/usuarios/${reserva.usuarioId}`);
            const horarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/horarios/${reserva.horarioId}`);

            return {
              ...reserva,
              asientoId: asientoResponse.data.n_asiento,
              usuarioId: usuarioResponse.data.nombre,
              horarioId: horarioResponse.data.hora_inicio,
            };
          } catch (error) {
            console.error('Error al obtener información adicional de reserva:', error);
            return null;
          }
        })
      );

      setData(updatedReservas.filter(Boolean));
    } catch (error) {
      console.error('Error al obtener datos de la API:', error);
    }
  };

  const handleImprimirManifiesto = () => {
    const reservasConfirmadas = data.filter((reserva) => reserva.confirmado);

    const doc = new jsPDF();
    doc.text('Manifiesto de Reservas Confirmadas', 20, 10);

    reservasConfirmadas.forEach((reserva, index) => {
      const yPosition = 20 + index * 10;
      doc.text(`Fecha: ${new Date(reserva.Fecha).toLocaleDateString()}`, 20, yPosition);
      doc.text(`Hora: ${new Date(reserva.Fecha).toLocaleTimeString()}`, 20, yPosition + 5);
      doc.text(`ID: ${reserva._id}`, 20, yPosition + 10);
      doc.text(`QR: ${reserva.qr}`, 20, yPosition + 15);
      doc.text(`Asiento: ${reserva.asientoId}`, 20, yPosition + 20);
      doc.text(`Usuario: ${reserva.usuarioId}`, 20, yPosition + 25);
      doc.text(`Horario: ${reserva.horarioId}`, 20, yPosition + 30);
      doc.text('----------------------------------------', 20, yPosition + 35);
    });

    doc.save('manifiesto_reservas.pdf');
  };

  return (
    <div>
      <h1>Tabla de Reservas</h1>
      <button onClick={handleImprimirManifiesto}>Imprimir Manifiesto</button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Fecha</th>
            <th>QR</th>
            <th>Asiento</th>
            <th>Usuario</th>
            <th>Horario</th>
            <th>Confirmado</th>
          </tr>
        </thead>
        <tbody>
          {data.map((reserva) => (
            <tr key={reserva._id}>
              <td>{reserva._id}</td>
              <td>{new Date(reserva.Fecha).toLocaleString()}</td>
              <td>{reserva.qr}</td>
              <td>{reserva.asientoId}</td>
              <td>{reserva.usuarioId}</td>
              <td>{reserva.horarioId}</td>
              <td>{reserva.confirmado ? 'Sí' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Reserva;
