import React, { useState } from 'react';
import MyImage from '../assets/img1.png';
import BusImage from '../assets/bus.png';
import TecsupImage from '../assets/tecsup.png';
import '../css/inicio.css';

import { useHistory } from 'react-router-dom';

export function Inicio() {
  const [usuario, setUsuario] = useState('');
  const [contraseña, setContraseña] = useState('');
  
  // Variable para controlar la visibilidad de la alerta de sesión exitosa
const [showAlert, setShowAlert] = useState(false);

  const history = useHistory();


  const handleSubmit = () => {
    // Lógica de inicio de sesión aquí...
  
    // Redirige a la página deseada
    history.push('/conductor');
  
    // Muestra la alerta
    setShowAlert(true);
  
    // Puedes agregar un temporizador para ocultar la alerta después de unos segundos
    setTimeout(() => {
      setShowAlert(false);
    }, 3000); // Ocultar la alerta después de 3 segundos (ajusta según tus necesidades)
  };
  




  return (
    <div className='inicio'>
      <div className='image'>
        <img className='image' src={MyImage} alt='login' />
      </div>
      <div className='content'>
        <div className='top-bar'>TECSUP</div>
        <div className='side-rectangle'>
          <div className='text-container'>
            <p className='text'>BusTecsup</p>
            <img src={BusImage} alt='bus' className='bus-image' />
            <img src={TecsupImage} alt='tecsup' className='tecsup-image' />
          </div>
          <label className='label' htmlFor='usuario'>
            Usuario
          </label>
          <input
            className='label'
            type='text'
            id='usuario'
            value={usuario}
            onChange={(e) => setUsuario(e.target.value)}
          />
          <label className='label' htmlFor='contraseña'>
            Contraseña
          </label>
          <input
            className='label'
            type='password'
            id='contraseña'
            value={contraseña}
            onChange={(e) => setContraseña(e.target.value)}
          />
       <div>
    {showAlert && (
      <div className="alert alert-success" role="alert">
        Sesión exitosa
      </div>
    )}

    <button
      className="btn btn-primary btn-lg btn-block"
      onClick={handleSubmit}
    >
      Iniciar Sesión
    </button>
  </div>
         
        </div>
      </div>
    </div>
  );
}

export default Inicio;
