import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/registro.css';
import jsPDF from 'jspdf';

const Reserva = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://api-node-bus.onrender.com/api/reservas');
      setData(response.data);
    } catch (error) {
      console.error('Error al obtener datos de la API:', error);
    }
  };

  const handleImprimirManifiesto = () => {
    const reservasConfirmadas = data.filter((reserva) => reserva.confirmado);

    const doc = new jsPDF();
    doc.text('Manifiesto de Reservas Confirmadas', 20, 10);

    const usuarioIdMap = {}; // Mapeo para usuarioId
    const asientoIdMap = {}; // Mapeo para asientoId
    const horarioIdMap = {}; // Mapeo para horarioId

    reservasConfirmadas.forEach((reserva, index) => {
      // Mapeo para usuarioId
      if (!usuarioIdMap[reserva.usuarioId]) {
        usuarioIdMap[reserva.usuarioId] = Object.keys(usuarioIdMap).length + 1;
      }
      // Mapeo para asientoId
      if (!asientoIdMap[reserva.asientoId]) {
        asientoIdMap[reserva.asientoId] = Object.keys(asientoIdMap).length + 1;
      }
      // Mapeo para horarioId
      if (!horarioIdMap[reserva.horarioId]) {
        horarioIdMap[reserva.horarioId] = Object.keys(horarioIdMap).length + 1;
      }

      const yPosition = 20 + index * 10;
      doc.text(`Fecha: ${new Date(reserva.Fecha).toLocaleDateString()}`, 20, yPosition);
      doc.text(`Hora: ${new Date(reserva.Fecha).toLocaleTimeString()}`, 20, yPosition + 5);
      doc.text(`ID: ${index + 1}`, 20, yPosition + 10);
      doc.text(`QR: ${reserva.qr}`, 20, yPosition + 15);
      doc.text(`Asiento ID: ${asientoIdMap[reserva.asientoId]}`, 20, yPosition + 20);
      doc.text(`Usuario ID: ${usuarioIdMap[reserva.usuarioId]}`, 20, yPosition + 25);
      doc.text(`Horario ID: ${horarioIdMap[reserva.horarioId]}`, 20, yPosition + 30);
      doc.text('----------------------------------------', 20, yPosition + 35);
    });

    doc.save('manifiesto_reservas.pdf');
  };

  return (
    <div>
      <h1>Tabla de Reservas</h1>
      <button onClick={handleImprimirManifiesto}>Imprimir Manifiesto</button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Fecha</th>
            <th>QR</th>
            <th>Asiento ID</th>
            <th>Usuario ID</th>
            <th>Horario ID</th>
            <th>Confirmado</th>
          </tr>
        </thead>
        <tbody>
          {data.map((reserva, index) => (
            <tr key={reserva._id}>
              <td>{index + 1}</td>
              <td>{new Date(reserva.Fecha).toLocaleString()}</td>
              <td>{reserva.qr}</td>
              <td>{reserva.asientoId}</td>
              <td>{reserva.usuarioId}</td>
              <td>{reserva.horarioId}</td>
              <td>{reserva.confirmado ? 'Sí' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Reserva;
