import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/registro.css';
import eliminarImg from '../assets/eliminar.png';
import editarImg from '../assets/editar.png';
import FlechaImg from '../assets/flecha.png';

function Reserva() {
  const [reservas, setReservas] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [editModalIsOpen, setEditModalIsOpen] = useState(false);
  const [selectedReserva, setSelectedReserva] = useState(null);

  useEffect(() => {
    // Lógica para obtener las reservas desde la API
    axios.get('https://api-node-bus.onrender.com/api/reservas')
      .then(response => setReservas(response.data))
      .catch(error => console.error('Error al obtener las reservas:', error));
  }, []);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const handleClickEliminar = (reservaId) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este elemento?')) {
      // Lógica para eliminar el elemento
      console.log('Elemento eliminado');
      // Aquí puedes realizar la lógica para eliminar el elemento utilizando el ID (reservaId)
    } else {
      console.log('Eliminación cancelada');
    }
  };

  const openEditModal = (reserva) => {
    setSelectedReserva(reserva);
    setEditModalIsOpen(true);
  };

  const closeEditModal = () => {
    setSelectedReserva(null);
    setEditModalIsOpen(false);
  };

  const handleClickFlecha = () => {
    // Lógica para manejar el clic en el botón "Eliminar"
  };

  const getAsientoInfo = async (asientoId) => {
    try {
      const response = await axios.get(`https://api-node-bus.onrender.com/api/asientos/${asientoId}`);
      return response.data.n_asiento;
    } catch (error) {
      console.error('Error al obtener información del asiento:', error);
      return 'N/A';
    }
  };

  const getUsuarioInfo = async (usuarioId) => {
    try {
      const response = await axios.get(`https://api-node-bus.onrender.com/api/usuarios/${usuarioId}`);
      return response.data.nombre;
    } catch (error) {
      console.error('Error al obtener información del usuario:', error);
      return 'N/A';
    }
  };

  const getHorarioInfo = async (horarioId) => {
    try {
      const response = await axios.get(`https://api-node-bus.onrender.com/api/horarios/${horarioId}`);
      return response.data.hora_inicio;
    } catch (error) {
      console.error('Error al obtener información del horario:', error);
      return 'N/A';
    }
  };

  return (
    <div className="inicio-container">
      <h2>Paraderos de BusTecsup</h2>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th colSpan="8">
                Buses
                <button className='btn1' onClick={openModal}>+ Añadir Paradero</button>
              </th>
            </tr>
            <tr>
              <th>Fecha<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>qr<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>asientoId<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Asiento<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>usuarioId<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Usuario<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>horarioId<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Hora de inicio<img className="flecha" src={FlechaImg} alt="Flecha" onClick={handleClickFlecha} /></th>
              <th>Editar&nbsp;&nbsp;&nbsp;&nbsp;Eliminar</th>
            </tr>
          </thead>
          <tbody>
            {reservas.map(async (reserva) => {
              const asientoInfo = await getAsientoInfo(reserva.asientoId);
              const usuarioInfo = await getUsuarioInfo(reserva.usuarioId);
              const horarioInfo = await getHorarioInfo(reserva.horarioId);

              return (
                <tr key={reserva._id}>
                  <td>{new Date(reserva.Fecha).toLocaleDateString()}</td>
                  <td>{reserva.qr}</td>
                  <td>{reserva.asientoId}</td>
                  <td>{asientoInfo}</td>
                  <td>{reserva.usuarioId}</td>
                  <td>{usuarioInfo}</td>
                  <td>{reserva.horarioId}</td>
                  <td>{horarioInfo}</td>
                  <td>
                    <button className='img' onClick={() => openEditModal(reserva)}>
                      <img className='img' src={editarImg} alt="Editar" />
                    </button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    <button className='img' onClick={() => handleClickEliminar(reserva._id)}>
                      <img className='img' src={eliminarImg} alt="Eliminar" />
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {modalIsOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h1>Registro de Paradero</h1>
            <form>
              {/* ... (Resto del formulario) */}
              <button className="submit1" type="submit">Agregar</button>
              <button className="submit2" type="button" onClick={closeModal}>Cancelar</button><br />
            </form>
          </div>
        </div>
      )}

      {editModalIsOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeEditModal}>
              &times;
            </span>
            <h1>Actualizar Paradero</h1>
            <form>
              {/* ... (Resto del formulario) */}
              <button className="submit1" type="submit">Actualizar</button>
              <button className="submit2" type="button" onClick={closeEditModal}>Cancelar</button><br />
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Reserva;
