import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/registro.css';
import jsPDF from 'jspdf';
import eliminarImg from '../assets/eliminar.png';
import FlechaImg from '../assets/flecha.png';

function Reserva() {
  const [reservas, setReservas] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const reservasData = await axios.get('https://api-node-bus.onrender.com/api/reservas');
      const reservas = reservasData.data;
  
      const updatedReservas = await Promise.all(reservas.map(async (reserva) => {
        try {
          const usuarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/usuarios/${reserva.usuarioId}`);
          const horarioResponse = await axios.get(`https://api-node-bus.onrender.com/api/horarios/${reserva.horarioId}`);
          const asientoResponse = await axios.get(`https://api-node-bus.onrender.com/api/asientos/${reserva.asientoId}`);
          
          return {
            ...reserva,
            asientoId: asientoResponse.data.n_asiento,
            usuarioId: usuarioResponse.data.nombre,
            horarioId: horarioResponse.data.hora_inicio,
          };
        } catch (error) {
          console.error('Error al obtener información adicional de reserva:', error);
          if (axios.isAxiosError(error) && error.response) {
            console.error('Código de estado de la respuesta:', error.response.status);
          }
          return null;
        }
        
      }));
  
      setReservas(updatedReservas.filter(Boolean));
    } catch (error) {
      console.error('Error al obtener las reservas:', error);
    }
  };
  
  const handleClickEliminar = async (reservaId) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este elemento?')) {
      try {
        await axios.delete(`https://api-node-bus.onrender.com/api/reservas/${reservaId}`);
        console.log('Elemento eliminado');
        fetchData();
      } catch (error) {
        console.error('Error al eliminar la reserva:', error);
      }
    } else {
      console.log('Eliminación cancelada');
    }
  };

  const imprimirManifiesto = () => {
    const reservasConfirmadas = reservas.filter(reserva => reserva.confirmado);
    const doc = new jsPDF();
    doc.text('Manifiesto de Reservas Confirmadas', 20, 10);
    reservasConfirmadas.forEach((reserva, index) => {
      const yPosition = 20 + index * 10;
      doc.text(`Fecha: ${new Date(reserva.Fecha).toLocaleDateString()}`, 20, yPosition);
      doc.text(`Hora: ${new Date(reserva.Fecha).toLocaleTimeString()}`, 20, yPosition + 5);
      doc.text(`Número de Asiento: ${reserva.asientoId}`, 20, yPosition + 10);
      doc.text(`Estudiante: ${reserva.usuarioId}`, 20, yPosition + 15);
      doc.text(`Horario: ${reserva.horarioId}`, 20, yPosition + 20);
      doc.text('----------------------------------------', 20, yPosition + 25);
    });
    doc.save('manifiesto_reservas.pdf');
  };

  return (
    <div className="inicio-container">
      <h2>Reservas de BusTecsup</h2>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th colSpan="7">
                Buses
                <button className='btn1' onClick={imprimirManifiesto}>Imprimir Manifiesto</button>
              </th>
            </tr>
            <tr>
              <th>Fecha<img className="flecha" src={FlechaImg} alt="Flecha" /></th>
              <th>qr<img className="flecha" src={FlechaImg} alt="Flecha" /></th>
              <th>Número de Asiento<img className="flecha" src={FlechaImg} alt="Flecha" /></th>
              <th>Estudiante<img className="flecha" src={FlechaImg} alt="Flecha" /></th>
              <th>Horario<img className="flecha" src={FlechaImg} alt="Flecha" /></th>
              <th>Confirmado</th>
              <th>Eliminar</th>
            </tr>
          </thead>
          <tbody>
            {reservas.length === 0 ? (
              <tr>
                <td colSpan="7">No hay reservas</td>
              </tr>
            ) : (
              reservas.map(reserva => (
                <tr key={reserva._id}>
                  <td>{new Date(reserva.Fecha).toLocaleDateString()}</td>
                  <td>{reserva.qr}</td>
                  <td>{reserva.asientoId}</td>
                  <td>{reserva.usuarioId}</td>
                  <td>{reserva.horarioId}</td>
                  <td>{reserva.confirmado ? 'Sí' : 'No'}</td>
                  <td>
                    <button className='img' onClick={() => handleClickEliminar(reserva._id)}>
                      <img className='img' src={eliminarImg} alt="Eliminar" />
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Reserva;
